import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import Card from '@mui/material/Card';
import CardActionArea from '@mui/material/CardActionArea';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import { RecipeCardProps } from '../modules/Explore/components/types';
import { useQuery } from 'react-query';

const useStyles = makeStyles({
  root: {
    maxWidth: 350,
    margin: 'auto',
    borderRadius: '16px',
    overflow: 'hidden',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  media: {
    height: 200,
    objectFit: 'cover',
  },
  content: {
    padding: '16px',
    backgroundColor: '#f4f4f4',
    borderTop: '1px solid #e2e2e2',
  },
  title: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
    color: '#222',
  },
  divider: {
    width: '100%',
    height: '2px',
    backgroundColor: '#e2e2e2',
    margin: '1rem 0',
  },
});

export const RecipeCard = ({ id, title, image }: RecipeCardProps) => {
  const classes = useStyles();
  const [expanded, setExpanded] = useState(false);

  // const { data: { summary } = {}, isFetching } = useQuery(['recipes', 'summary', id], async () => {
  //   const res = await fetch(
  //     `https://api.spoonacular.com/recipes/${id}/summary?apiKey=3a919f863b6f473e93b2473cdd0b6e3d&query=pasta&maxFat=25&number=16`,
  //   );
  //   return res.json();
  // });

  const summary =
    'Apple Cake might be a good recipe to expand your dessert recipe box. This recipe serves 12 and costs 62 cents per serving. One serving contains <b>366 calories</b>, <b>5g of protein</b>, and <b>13g of fat</b>. Only a few people made this recipe, and 2 would say it hit the spot. If you have vanilla, oil, flour, and a few other ingredients on hand, you can make it. It is a good option if you\'re following a <b>dairy free and lacto ovo vegetarian</b> diet. From preparation to the plate, this recipe takes about <b>45 minutes</b>. It is brought to you by Foodista. Taking all factors into account, this recipe <b>earns a spoonacular score of 33%</b>, which is not so super. If you like this recipe, you might also like recipes such as <a href="https://spoonacular.com/recipes/apple-pumpkin-sour-cream-coffee-cake-with-apple-cider-glaze-63159">Apple Pumpkin Sour Cream Coffee Cake With Apple Cider Glaze</a>, <a href="https://spoonacular.com/recipes/spiced-rum-apple-cake-with-apple-cider-caramel-glaze-1242767">Spiced Rum Apple Cake with Apple Cider Caramel Glaze</a>, and <a href="https://spoonacular.com/recipes/spiced-rum-apple-cake-with-apple-cider-caramel-glaze-588384">Spiced Rum Apple Cake with Apple Cider Caramel Glaze</a>.';

  const summaryMarkup = { __html: summary };

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia className={classes.media} image={image} title={title} />
        <div className={classes.content}>
          <Grid container alignItems='center'>
            <Grid item xs>
              <Typography variant='h5' component='h2' className={classes.title}>
                {title}
              </Typography>
            </Grid>
          </Grid>
          <div className={classes.divider}></div>
          <Typography variant='body2' color='textSecondary' component='p'>
            {isFetching ? (
              'Loading...'
            ) : (
              <>
                <div
                  dangerouslySetInnerHTML={
                    expanded ? summaryMarkup : { __html: `${summary.substring(0, 200)}...` }
                  }
                />
                {summary.length > 200 && (
                  <span onClick={() => setExpanded(!expanded)}>
                    {expanded ? 'Read less' : 'Read more'}
                  </span>
                )}
              </>
            )}
          </Typography>
        </div>
      </CardActionArea>
    </Card>
  );
};
