import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import Card from '@mui/material/Card';
import CardActionArea from '@mui/material/CardActionArea';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import { RecipeCardProps } from '../modules/Explore/components/types';
import { useQuery } from 'react-query';

const useStyles = makeStyles({
  root: {
    maxWidth: 350,
    margin: 'auto',
    borderRadius: '16px',
    overflow: 'hidden',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: '0.3s',
    '&:hover': {
      transform: 'scale(1.1)',
    },
  },
  media: {
    height: 200,
    objectFit: 'cover',
  },
  content: {
    padding: '16px',
    backgroundColor: '#f4f4f4',
    borderTop: '1px solid #e2e2e2',
  },
  title: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
    color: '#222',
  },
  divider: {
    width: '100%',
    height: '2px',
    backgroundColor: '#e2e2e2',
    margin: '1rem 0',
  },
});

export const RecipeCard = ({ id, title, image }: RecipeCardProps) => {
  const classes = useStyles();
  const [expanded, setExpanded] = useState(false);
  const [truncatedSummary, setTruncatedSummary] = useState('');

  const { data: { summary } = {}, isFetching } = useQuery(['recipes', 'summary', id], async () => {
    const res = await fetch(
      `https://api.spoonacular.com/recipes/${id}/summary?apiKey=3a919f863b6f473e93b2473cdd0b6e3d&query=pasta&maxFat=25&number=16`,
    );
    const { summary } = await res.json();
    setTruncatedSummary(summary.substring(0, 200) + '... ');
    return { summary };
  });

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia className={classes.media} image={image} title={title} />
        <div className={classes.content}>
          <Grid container alignItems='center'>
            <Grid item xs>
              <Typography variant='h5' component='h2' className={classes.title}>
                {title}
              </Typography>
            </Grid>
          </Grid>
          <div className={classes.divider}></div>
          <Typography variant='body2' color='textSecondary' component='p'>
            {isFetching ? 'Loading...' : expanded ? summary : truncatedSummary}
            {summary.length > 200 && (
              <span onClick={() => setExpanded(!expanded)}>
                {expanded ? 'Read less' : 'Read more'}
              </span>
            )}
          </Typography>
        </div>
      </CardActionArea>
    </Card>
  );
};
