import { apiConfig } from '../../config';

const { baseUrl, apiKey } = apiConfig.spoonacular;

export const fetcher = async (path: string) => {
  const url = `${baseUrl}${path}?apiKey=${apiKey}`;
  const res = await fetch(url);
  return res.json();
};
